import React from 'react';
import { Link } from 'react-router-dom';
import { useState } from 'react';

import { signInWithEmailAndPassword, onAuthStateChanged, signOut } from "firebase/auth";
import { auth } from '../firebase';

const SigninPage = () => {

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  onAuthStateChanged(auth, (currentUser) => {

  })

  signInWithEmailAndPassword(auth, email, password)
    .then((userCredential) => {
      // Signed in 
      const user = userCredential.user;
      alert("logged in");
      // ...
    })
    .catch((error) => {
      const errorCode = error.code;
      const errorMessage = error.message;
      alert(errorMessage);
    });

    login()

  return (
    <div className='signinContainer'>
      <div className='signinFormWrap'>
        <Link className='signinIcon' to='/'>ReunionDouglas</Link>
        <div className='signinFormContent'>
          <form className='singinForm' action='#'>
            <h1 className='signinFormH1'>Sign in to your account</h1>
            <label className='signinFormLabel' htmlFor='for'>Email</label>
            <input className='signinFormInput' type={"email"} required onChange={(event) => setEmail(event.target.value)}/>
            <label className='signinFormLabel' htmlFor='for'>Password</label>
            <input className='signinFormInput' type={"password"} required onChange={(event) => setPassword(event.target.value)}/>
            <button className='signinFormButton' onClick={login}>Continue</button>
            <button className='signinFormButton' onClick={logout}>LogOut</button>
            <span className='signinFormText'>Forgot password</span>
          </form>
        </div>
      </div>
    </div>
  );
}

export default SigninPage;
